mapper.select:
SELECT uid,event_id as eid,status FROM t_join
                                  where uid = #{uid}

                                    and event_id = #{eid}

;

mapper.update:
update  t_join set
    status =  ${status}
where event_id = ${eid} and uid = ${uid}

mapper.select:
select tu.name,t.uid from t_join as t left join event_res.t_user tu on t.uid = tu.uid where event_id = #{eid}

    and status = #{status}

;
